var data=context.get("data");
var power=msg.payload.power;
var maxLength=10;
data.push(power);
var count=data.length;
if(count > maxLength){
    data.shift();
    count=data.length;
}
context.set("data",data);

var avg=0.0;
for(var i=0;i<count;i++){
    avg+=data[i];
}
avg/=count;
msg.payload=avg;
var New=-1;
if(avg <2.0){
    New=0;
} else if (avg >= 2){
    New=1;
}
var Old=flow.get("Old");
msg.payload="";
msg.new=New;
msg.old=Old;
msg.avg=avg;
msg.topic="Washing Machine"
if(Old < 0) {
    flow.set("Old",New);
    return [null,null,msg];
} 
if(Old == 0 && New == 1){
    msg.payload="Sir, The washing machine started its cycle.";
    flow.set("Old",New);
    return [msg,null,null];
}
if(Old == 1 && New == 0){
    msg.payload="Sir, The washing machine finished its cycle.";
    flow.set("Old",New);
    return [null,msg,null];
}
return [null,null,msg];

